function(state) {
  // state to be passed and to be used as needed
  let text = sig("Hello world")
  let dir_exist = sig(false)
  let dirs = sig([])
  let button = () => {
    let x = sig(2)

    return h("button", {
      style: `
        padding: 2em;
        margin: 1em;
        margin-left: 0;
        background: grey;
      `,

      onclick: (e) => {
        e.stopPropagation()
        x.set(x() + 1)
      }

    }, x)
  }

  let span_style = `
    font-size: .5em;
    color: #ff00aa88;
    opacity: .5;
  `


  setTimeout(() => { refresh() }, 1500)

  let refresh = () => {
    if (m() && m().ass){
      eff_on(m().ass, () => {

        text.set(m().ass())
      })
    }
    if (m() && m().dir) {
      eff_on(() => m().dir, () => {
        dir_exist.set(true) 
        console.log(m().dir)
        dirs.set([...m().dir.files, ...m().dir.dirs]) 
      })
    }
  }

  let refresh_button = () => h("button", {onclick: refresh}, "refresh")


  let dir_elems =  mem(() => dirs()
    .map((el) => {
      let name = el
      console.log(name)
      console.log("path", m().path()) 

      return h("div",
        h("button", { 
          onclick: () => {
            m().path.set(m().path()+"/"+name);
            refresh()
          }
        }, name)
      )}
    ))

  return [
    h("h1", text, 
      h("span", {style:span_style},  
        mem(() => dir_exist() ?  "(wink wonk)": ""  ) 
       ),
      refresh_button()
     ),

    dir_elems
  ]
}

