import {eff_on, sig, mut, h} from "/lib/solid/monke.js"

const defer = (fn, t = 400) => setTimeout(fn, t)
const M = mut({})
document.M = M

M.ass = sig("funky")
M.path = sig("")

eff_on(M.path,() => check_dir(M.path()))
setTimeout(() => M.ass.set("FICKITY"), 2500)

function load_path(path){
  if (M) { if (M.EDITOR) M.EDITOR.state.load(path) }
}

function save_path(path){
  if (M) { if (M.EDITOR) M.EDITOR.state.save(path) }
}

function show_dir(path, dir){
  let path_split = path.split("/")
  let path_slice = path_split.slice(0, path_split.length - 2)
  let back_path = path_slice.join("/")
  let title = path == "" ? "" : "../" + back_path
  let back = p(title, () => check_dir(back_path))

  M.dir = dir

  let files = dir.files.map((f) => p(f, () => M.path.set(path+"/"+f)))
  let dirs = dir.dirs.map((f) => dir_elem(f, () => M.path.set(path+"/"+f)))

  document.body.innerHTML = ""
  document.body.appendChild(back)
  dirs.forEach((f) => document.body.appendChild(f))
  files.forEach((f) => document.body.appendChild(f))
}

function show_file(src) {
  if (src.split(".").pop() == "jpg"){
    document.body.innerHTML = `<img src="${src}"></img>`
  }
  console.log("src", src)
}

function dir_elem(text, onclick){
  let style = {
    "font-family": "monospace",
    border: `1px solid white`,
    color: "white",
    padding: `10px`,
    width: 'min-content',
    "min-width": '300px',
    cursor: "pointer"
  }


  return  h("p", {style, onclick}, text )()
}

function p(text, onclick){
  let style = {
    "font-family": "monospace",
    color: "white",
    width: 'min-content',
    "min-width": '300px',
    cursor: "pointer",
    background: "#ff00aa88",
    opacity: .5,
  }

  return h("p", {style, onclick}, text)()
}

function check_dir(path = "", root = "/fs/"){
  fetch(root+path)
    .then((res) => {
      console.log("got res")
      try{ let r = res.json(); if(r) return r; else return null }
      catch { return null }
      return null
    })
    .then((res) => {
      console.log(res)
      if (!res) console.log("failed")
      if (res.type == "dir") show_dir(path, res) 
      else show_file(root+path)
    })
}
