class Book {
  /**
  @param {Spread[]} [spreads=[]] 
  @param {{draw_grid: boolean}=} opts
  */
  constructor(spreads = [], opts = {draw_grid:true}){
    this.grid = opts.draw_grid
    this.current_page = 0
    /**@type Spread[]*/
    this.spreads = spreads
  }

  /**@param {Spread} spread */
  add_spread(spread){
    this.spreads.push(spread)
  }

  draw(p){
    if(this.grid) this.current_spread().draw_grid(p, this.current_page)
    this.current_spread().draw(p)
  }

  /**
  @typedef {p5.Image} Image
  */
  get_verso(p, number){
    let _p = p.createGraphics(p.width, p.height)
    if (this.grid) this.spreads[number].draw_grid(_p)
    this.spreads[number].draw(_p)
    let img = _p.get(0,0, _p.width/2, _p.height)

    return img
  }

  /**
  @typedef {p5.Image} Image
  */
  get_recto(p, number){
    let _p = p.createGraphics(p.width, p.height)
    if (this.grid) this.spreads[number].draw_grid(_p)
    this.spreads[number].draw(_p)
    let img = _p.get(_p.width/2, 0, _p.width/2, _p.height)
    return img
  }

  /**
  @param {Image} img  
  */
  draw_img(p, img, x=0, y=0){
    p.image(img, x, y, img.width, img.height)
  }

  draw_verso(p){
    let img = this.get_verso(p, this.current_page)
    this.draw_img(p, img)
  }

  draw_recto(p){
    let img = this.get_recto(p, this.current_page)
    this.draw_img(p, img, img.width)
  }

  current_spread() {
    return this.spreads[this.current_page]
  }

  seek(page){
    this.current_page = page
  }


}

